  height: 100%;
  font-family: "Lexend Deca", sans-serif;
}

a {
  text-decoration: none;
  color: #333;
}

header {
  height: 10%;
  min-height: 80px;
  width: 100%;
  min-width: 1550px;
  -webkit-box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.12);
}
header .inner {
  position: relative;
  width: 95%;
  height: 100%;
  display: flex;
  justify-content: space-between;
  margin: auto;
}
header .inner .logo {
  display: flex;
  align-items: center;
}
header .inner .logo img {
  height: 75%;
}
header .inner .logo .title h1 {
  font-size: 35px;
  font-weight: 700;
  margin-bottom: 8px;
  color: #49277f;
}
header .inner .logo .title .user-name {
  display: flex;
  align-items: flex-end;
  justify-content: end;
  min-height: 85%;
  color: #333;
}
header .inner .logo .title .user-name .name {
  font-size: 20px;
  white-space: nowrap;
}
header .inner .logo .title .user-name .logout {
  font-size: 13px;
  padding-left: 8px;
  white-space: nowrap;
}
header .inner .logo .title .user-name .logout a {
  color: #ffd801;
}
header .inner nav {
  display: flex;
  align-items: flex-end;
  height: 85%;
  width: 1250px;
  justify-content: center;
}
header .inner nav ul {
  display: flex;
}
header .inner nav ul li {
  padding: 0 25px;
  font-size: 18px;
  white-space: nowrap;
}
header .inner nav ul li a {
  color: #999;
  font-weight: 600;
  position: relative;
  transition: all 0.3s;
}
header .inner nav ul li a:hover {
  color: #49277f;
}
header .inner nav ul li a::after {
  content: "";
  position: absolute;
  left: 50%;
  bottom: -4px;
  width: 0;
  height: 2px;
  background-color: #49277f;
  transition: all 0.6s;
}
header .inner nav ul li a::before {
  content: "";
  position: absolute;
  right: 50%;
  bottom: -4px;
  width: 0;
  height: 2px;
  background-color: #49277f;
  transition: all 0.6s;
}
header .inner nav ul li a:hover::after, header .inner nav ul li a:hover::before {
  width: 50%;
}
header .inner nav ul li #underline {
  color: #49277f;
}
header .inner nav ul li #underline::after, header .inner nav ul li #underline::before {
  width: 50%;
}
header .inner nav ul li:last-child {
  padding-right: 10px;
}

