  width: 100%;
  height: 70%;
  min-height: 600px;
  display: flex;
  position: relative;
  min-width: 1550px;
}
section aside {
  width: 20%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  align-items: center;
}
section aside .table {
  width: 95%;
  height: 90%;
  border: 2px solid #49277f;
  border-radius: 10px;
  background-color: white;
  text-align: center;
}
section aside .table .task {
  border-bottom: 2px solid black;
  padding: 5px 0;
}
section aside .table .task h2 {
  font-size: 20px;
  color: #333;
}
section aside .btn {
  width: 95%;
  height: 5%;
  display: flex;
  justify-content: space-between;
}
section aside .btn button {
  width: 30%;
  background-color: white;
  border: 2px solid #49277f;
  border-radius: 10px;
  font-size: 15px;
  color: #333;
  font-family: "Lexend Deca", sans-serif;
  font-weight: 600;
}
section article {
  width: 80%;
  height: 100%;
  background-color: #ffffe0;
  z-index: -1;
}

footer {
  height: 20%;
  min-height: 100px;
  background-color: #ccc;
  min-width: 1550px;
}

